mapper.update:
update act_order
set status = #{status}
where order_sn = #{orderSn}

mapper.select:
select o.id, o.member_id, o.order_sn, o.member_username, o.total_amount, o.pay_amount, o.promotion_amount, o.pay_type, o.status, o.receiver_name, o.receiver_phone, o.receiver_detail_address, o.confirm_status, o.delete_status, o.payment_time, o.delivery_time, o.express_company, o.express_sn, o.receive_time, o.comment_time, o.create_time, o.update_time as updateTime, oi.act_id as act_id, oi.act_name as act_name, oi.club_id as club_id from act_order o left join act_order_item oi on o.id = oi.order_id
o.member_id = #{memberId}
and o.status = #{orderStatus}

mapper.update:
update act_order
set status=#{status}
where id in

  #{id}
